import unittest
import string

str = "Line1- a\n\nLine3- b\nLine4- c"
str_1 = "Line1- a\n\nLine3- b\nLine4- "
str_2 = "\n\n"
str_3 = ""

class SplitlinesTest(unittest.TestCase):
    def test_splitlines(self):
        self.assertEqual(str.splitlines(), ["Line1- a", "", "Line3- b", "Line4- c"])
        self.assertEqual(str.splitlines(True), ["Line1- a\n", "\n", "Line3- b\n", "Line4- c"])
        self.assertEqual(str_1.splitlines(), ["Line1- a", "", "Line3- b", "Line4- "])
        self.assertEqual(str_2.splitlines(), ["",""])
        self.assertEqual(str_2.splitlines(True), ["\n", "\n"])
        self.assertEqual(str_3.splitlines(), [])
        self.assertEqual(str_3.splitlines(True), [])
        self.assertRaises(TypeError, str.splitlines, True, "b")
        self.assertRaises(TypeError, str.splitlines, True, "@")
        self.assertRaises(TypeError, str.splitlines, True, 1)


if __name__ == '__main__':
    unittest.main()
